import React, {useState} from "react";

function App() {

  const [heading, setHeading] = useState("hello");
  function headingSub(){
    setHeading("Input here")
  }
  return (
    <div className="container">
      <h1>{heading}</h1>
      <input type="text" placeholder="What's your name?" />
      <button
      onClick={headingSub}
      >Submit</button>
    </div>
  );
}

export default App;
